import React from "react";
import styled from "styled-components";

const InputPage = (props) => {
    const my_wrap = React.useRef(null);


    return(

        <Styles ref={my_wrap}>
            <h1>단어 추가하기</h1>
            <br/>
            <div>
                <p style = {{ textAlign: "center"}}>단어</p>
                <input/>
            </div>
            <div>
                <p>설명</p>
                <input/>
            </div>
            <div>
                <p>예시</p>
                <input type="text"/>
            </div>
            <Add>추가하기</Add>
        </Styles>
    )
};




//div
const Styles = styled.div`
    border: 1px solid #eee;
    padding: 20px;
    width: 400px;
    margin: 30px auto;
`;

//button
const Add = styled.button`
    padding: 10px 36px;
    backgroundColor: #dadafc;
    border: #dadafc;
    borderRadius: 30px;
    margin: 36px 0px;
    position: absolute;
`;


export default InputPage;
