import React from "react";
import styled from "styled-components";

const Detail = (props) => {
    const my_wrap = React.useRef(null);


    return(

        <div ref={my_wrap}>
            <h1>단어 추가하기</h1>
            <br />
            <div>
                <p>단어</p>
                <input type="text" ref={props.word_ref} />
            </div>
            <div>
                <p>설명</p>
                <input type="text" ref={props.explain_ref} />
            </div>
            <div>
                <p>예시</p>
                <input type="text" ref={props.example_ref} />
            </div>
            <Add>추가하기</Add>
        </div>
    )
};




// //div
