import React from "react";
import styled from "styled-components";

const InputPage = (props) => {
    const my_wrap = React.useRef(null);


    return(

        <div ref={my_wrap}>
            <h1>단어 추가하기</h1>
            <div>
                <p>단어</p>
                <input/>
            </div>
            <div>
                <p>설명</p>
                <input/>
            </div>
            <div>
                <p>예시</p>
                <input type="text"/>
            </div>
            <button style={{
          padding: "10px 36px",
          backgroundColor: "#dadafc",
          border: "#dadafc",
          borderRadius: "30px",
          margin: "36px 0px"
      }}>추가하기</button>
        </div>
    )
};




const Card = styled.div`
    margin: 20px;
    margin-top: 50px;
    padding: 20px;
    width: 500px;
    min-height: 200px;
    display: grid;
    grid-template-rows: 20px 50px 1fr 50px;
    border-radius: 10px;
    box-shadow: 0px 6px 10px rgba(0, 0, 0, 0.25);
    transition: all 0.2s;

`;




export default InputPage;
