import styled from "styled-components";

const InputPage = () => {

    return(

        <div className = "InputPage">
            <Card>
            <h4>단어</h4>
            <h4>설명</h4>
            <h4>예시</h4>
        </Card>
        </div>
    )
};




const Card = styled.div`
    margin: 20px;
    margin-top: 30px;
    padding: 20px;
    width: 500px;
    min-height: 200px;
    display: grid;
    grid-template-rows: 20px 50px 1fr 50px;
    border-radius: 10px;
    box-shadow: 0px 6px 10px rgba(0, 0, 0, 0.25);
    transition: all 0.2s;

`;




export default InputPage;
